
function getTimeDisplay(ts){var months=['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];var ap="";var hour=ts.getHours();var minute=ts.getMinutes();var todaysDate=new Date();var todays12am=todaysDate.getTime()-(todaysDate.getTime()%(24*60*60*1000));var date=ts.getDate();var month=ts.getMonth();var armyTime='0';if(armyTime!=1){ap=hour>11?"pm":"am";hour=hour==0?12:hour>12?hour-12:hour;}else{hour=hour<10?"0"+hour:hour;}
minute=minute<10?"0"+minute:minute;var type='th';if(date==1||date==21||date==31){type='st';}
else if(date==2||date==22){type='nd';}
else if(date==3||date==23){type='rd';}
if(ts<todays12am){return hour+":"+minute+ap+' '+date+type+' '+months[month];}else{return hour+":"+minute+ap;}}
function getChatLog(id,chatroommode,basedata){jqcc.ajax({url:"chathistory.php?action=logs",data:{history:id,chatroommode:chatroommode,basedata:basedata},type:'post',dataType:'json',async:false,timeout:10000,success:function(data){if(data!='0'){temp='';jqcc.each(data,function(type,item){var message=item.message;if((item.message).indexOf('CC^CONTROL_')!=-1){message=parent.jqcc.cometchat.processcontrolmessage(item);}
temp=temp+'<div class="chat" id="'+item.id+'|'+item.previd+'" title="Haga clic aquí para ver toda la conversación"><div class="chatrequest"><b>'+item.from+'</b></div><div class="chatmessage chatmessage_short">'+message+'</div><div class="chattime" >'+getTimeDisplay(new Date(item.sent))+'</div><div style="clear:both"></div></div>';});jqcc('.container_body_chat').html('<div id ="logs">'+temp+'</div>');jqcc("#logs").slimScroll({height:'460px'}).css({height:'460px',width:'auto'});jqcc('.chat').click(function(){var range=jqcc(this).attr('id');getChatLogView(id,range,chatroommode,basedata);});}else{jqcc('.container_body_chat').html(norecords);}},error:function(data){}});}
function getChatLogView(id,range,chatroommode,basedata){var temp='<div class="chatbar"><div class="chatbar_1"></div><div class="chatbar_2"><a href="index.php?chatroommode='+chatroommode+'&logs=1&history='+id+'&basedata='+basedata+'&embed=web">Regresar</a></div><div style="clear:both"></div></div><div class="chatbar_body" id="chat_body"></div><div class="chathistory" title="Ver más conversación"><span>Ver más conversación</span><span class="arrowdown"></span></div>';jqcc('.container_body_chat').html(temp);jqcc(".chatbar_body").slimScroll({height:'410px'}).css({height:'410px',width:'auto'});getMessage(id,range,chatroommode,basedata);}
function getMessage(id,range,chatroommode,basedata,lastmessageid){var previousid=0;var name='';var time='';var count=0;var temp='';if(typeof(lastmessageid)=='undefined'){lastmessageid=0;}
jqcc.ajax({url:"chathistory.php?action=logview",data:{history:id,range:range,chatroommode:chatroommode,basedata:basedata,lastidfrom:lastmessageid},type:'post',dataType:'json',async:true,timeout:10000,success:function(data){var i=0;jqcc.each(data,function(type,item){var message=item.message;if((item.message).indexOf('CC^CONTROL_')!=-1){message=parent.jqcc.cometchat.processcontrolmessage(item);}
temp+='<div class="chat chatnoline" id="'+item.id+'"><div class="chatrequest"><b>'+item.from+'</b></div><div class="chatmessage chatnowrap">'+message+'</div><div class="chattime">'+getTimeDisplay(new Date(item.sent))+'</div><div style="clear:both"></div></div>';previousid=item.previd;name=item.requester;time=item.sent;i++;userid=item.userid;});jqcc('#chat_body').append(temp);count=jqcc('#messageCount').text();if(count==''){count=i;}else{count=parseInt(count)+parseInt(i);}
jqcc('div.chatbar_1').html('Conversación de Chat con '+name+' el <small class="chattimedate">'+getTimeDisplay(new Date(time))+'</small> (<span id="messageCount">'+count+'</span> líneas)');jqcc("div.chathistory").click(function(){var lastidfrom=parseInt(jqcc('div.chatnoline:last').attr('id'))+1;getMessage(id,lastidfrom+'|'+previousid,chatroommode,basedata,userid);jqcc("div.chathistory").unbind('click');});var cometchat_chathistory=jqcc('div.chatbar_body');cometchat_chathistory.scrollTop(cometchat_chathistory[0].scrollHeight-cometchat_chathistory.height());if(i<13){jqcc("div.chathistory").unbind('click').html(norecords).attr('title',' No conversación anterior para ver la');}},error:function(data){}});}